import axios from 'axios';

const creatBookAction = (book) => {
  axios.post('api/v1/books', { title: book.title, author: '', category: book.category })
    .then(response => {
      console.log(response.data)

    })
  return {
    type: 'CREATE_BOOK',
    book,
  }
};

const removeBookAction = (book) => ({
  type: 'REMOVE_BOOK',
  id: book.id,
});

const getBooksAction = (books) => {

  function request() {
    return axios.get('api/v1/books').then(response => {
      return response.data;
    })
      .catch((error => {
        console.error(error);
        return books
      }))
  }
  request().then(data => {
    return {
      type: 'GET_BOOKS',
      books: data,
    }
  })
}

const changeFilterAction = (filter) => ({
  type: 'CHANGE_FILTER',
  filter,
});

export { creatBookAction, removeBookAction, changeFilterAction, getBooksAction };
