import axios from 'axios';

const creatBookAction = (book) => ({
  type: 'CREATE_BOOK',
  book,
});

const removeBookAction = (book) => ({
  type: 'REMOVE_BOOK',
  id: book.id,
});

const getBooksAction = () => {
  let request;
  axios.get('api/v1/books').then(response => {

    request = response.data;
    // console.log(request)
  }).catch(error => {
    request = [];
    console.error('shit');
  })
  console.log(request)
  return {
    type: 'GET_BOOKS',
    books: request,
  }
}

const changeFilterAction = (filter) => ({
  type: 'CHANGE_FILTER',
  filter,
});

export { creatBookAction, removeBookAction, changeFilterAction, getBooksAction };
