import React from 'react';
import { Button } from './styled-components';

const Book = ({ item, handleRemoval }) => {
  const book = item;
  return (
    <tr>
      <td>{book.id}
        {book.title}
        {book.category}
      </td>
      <td>
        <Button
          type="button"
          text="Remove"
          handleClick={() => {
            handleRemoval(book);
          }} />
      </td>
    </tr>
  );
};


export default Book;
