import React from 'react';
import { connect } from 'react-redux';

const Book = ({ item, handleRemoveBook }) => {
  const book = item;
  return (
    <tr>
      <td>{book.id}</td>
      <td>{book.title}</td>
      <td>{book.category}</td>
      <td><button onClick={() => {
        handleRemoveBook(book)
      }}>Remove</button></td>
    </tr>
  );
};

const mapDispatchToProps = (dispatch) => ({
  handleRemoveBook: (book) => {
    dispatch(removeBookAction(book));
  }
});

Book = connect(null, mapDispatchToProps)(Book);

export default Book;
