const creatBookAction = (book) => ({
  type: 'CREATE_BOOK',
  book: book
})

const removeBookAction = (book) => {
  return {
    type: 'REMOVE_BOOK',
    book: book.id
  }
}

export { creatBookAction, removeBookAction };
