import axios from 'axios';

const creatBookAction = (book) => {
  axios.post('api/v1/books', { title: book.title, author: '', category: book.category })
    .then(response => {
      console.log(response.data)

    })
  return {
    type: 'CREATE_BOOK',
    book,
  }
};

const removeBookAction = (book) => ({
  type: 'REMOVE_BOOK',
  id: book.id,
});

const getBooksAction = (dispatch) => {
  let books;
  axios.get('api/v1/books')
    .then(response => {
      books = response.data;
    })

  dispatch({
    type: 'GET_BOOKS',
    books,
  });
}

const changeFilterAction = (filter) => ({
  type: 'CHANGE_FILTER',
  filter,
});

export { creatBookAction, removeBookAction, changeFilterAction, getBooksAction };
