import axios from 'axios';

const creatBookAction = (book) => {
  axios.post('api/v1/books', { title: book.title, author: '', category: book.category })
    .then(response => {
      console.log(response)
      return {
        type: 'CREATE_BOOK',
        book: response.data,
      }
    })
    .catch(error => {
      console.error(error);
      return {
        type: 'CREATE_BOOK',
        book: {},
      }

    })

};

const removeBookAction = (book) => ({
  type: 'REMOVE_BOOK',
  id: book.id,
});

const getBooksAction = () => {
  let request = axios.get('api/v1/books')

  return {
    type: 'GET_BOOKS',
    request: request,
  }
}

const changeFilterAction = (filter) => ({
  type: 'CHANGE_FILTER',
  filter,
});

export { creatBookAction, removeBookAction, changeFilterAction, getBooksAction };
