import React from 'react';
import { Button } from './styled-components';

const Book = ({ item, handleRemoval }) => {
  const book = item;
  return (
    <tr>
      <td>{book.id}</td>
      <td>{book.title}</td>
      <td>{book.category}</td>
      <td>
        <Button
          text="button"
          handleClick={() => {
            handleRemoval(book);
          }}>
          Remove
        </Button>
      </td>
    </tr>
  );
};


export default Book;
