import axios from 'axios';

const creatBookAction = (book) => ({
  type: 'CREATE_BOOK',
  book,
});

const removeBookAction = (book) => ({
  type: 'REMOVE_BOOK',
  id: book.id,
});

const getBooksAction = () => {
  const request = axios.get('api/v1/books')
    .then(response => {
      return response.data;
    })
    .catch(error => console.log(error));
  return {
    type: 'GET_BOOKS',
    books: request,
  }
}

const changeFilterAction = (filter) => ({
  type: 'CHANGE_FILTER',
  filter,
});

export { creatBookAction, removeBookAction, changeFilterAction, getBooksAction };
