import axios from 'axios';

const creatBookAction = (book) => {
  axios.post('api/v1/books', { title: book.title, author: '', category: book.category })
    .then(response => {
      console.log(response.data)

    })
  return {
    type: 'CREATE_BOOK',
    book,
  }
};

const removeBookAction = (book) => ({
  type: 'REMOVE_BOOK',
  id: book.id,
});

const getBooksAction = (books) => {
  let newBooks;
  const assignVar = (data) => {
    newBooks = data;
  }

  axios.get('api/v1/books').then(response => {
    assignVar(response.data)
  })
    .catch((error => {
      console.error(error);
      assignVar(books);
    }))
  console.log(newBooks)
  return {
    type: 'GET_BOOKS',
    books: newBooks,
  }
}

const changeFilterAction = (filter) => ({
  type: 'CHANGE_FILTER',
  filter,
});

export { creatBookAction, removeBookAction, changeFilterAction, getBooksAction };
