import React from 'react';
import { Button } from './styled-components';

const Book = ({ item, handleRemoval }) => {
  const book = item;
  return (
    <tr>
      <td>
        <span>{book.category}</span>
        <span>{book.title}</span>
        <span><em>Id:</em>{book.id}</span>
      </td>
      <td>
        <Button
          type="button"
          text="Remove"
          handleClick={() => {
            handleRemoval(book);
          }} />
      </td>
    </tr>
  );
};


export default Book;
