import React from 'react';
import BooksTable from '../components/books-table';
import BookForm from './book-form';
import CategoryFilter from './category-filter';
import Navigation from './navigation';

const containerStyle = {
  border: '1px solid',
  margin: '3rem',
  height: '80vh',
}


const rootStyle = {
  height: '100vh',
  display: 'grid',
  placeContent: 'center',
}
const App = () => (
  <div style={rootStyle}>
    <div>
      <Navigation />
      <BooksTable />
      <CategoryFilter />
      <BookForm />
    </div>
  </div>
);

export default App;
