import React from 'react';
import { connect } from 'react-redux';
import { creatBookAction, removeBookAction } from '../actions/index';

const categories = ['Action', 'Biography', 'History', 'Horror', 'Kids', 'Learning', 'Sci-Fi'];
let identifier = 0;

class BookForm extends React.Component {
  constructor(props) {
    super(props);
    //this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.state = {
      title: '',
      category: '',
    }
  }

  handleFormSubmit = (e) => {
    e.preventDefault();
    /* const { handleAdding } = this.props;
     const book = {
       id: identifier,
       title: this.titleInput.value,
       category: categories[this.categoryValue.options.selectedIndex],
     };
     identifier += 1;
     handleAdding(book);
     this.titleInput.value = '';
     this.categoryValue.options.selectedIndex = 0;*/
    handleAdding(Object.assign({ id: identifier }, this.state));
    identifier++;
    this.setState({
      title: '',
      category: '',
    })
  }

  handleChange = (e) => {
    const { target, value } = e
    switch (target.localName) {
      case 'input':
        this.setState((state) => (Object.assign({}, state, { input: value })));
        break;
      default:
        this.setState((state) => (Object.assign({}, state, { category: value })));
        break;
    }
  }

  render() {
    return (
      <form onSubmit={this.handleFormSubmit}>
        <div>
          <label htmlFor="title">Book Title</label>
          <input ref={(node) => { this.titleInput = node; }} onChange={this.handleChange} type="text" id="title" value={this.state.input} />
        </div>
        <div>
          <label htmlFor="ctg">Book Category</label>
          <select name="category" id="ctg" ref={(node) => { this.categoryValue = node; }} onChange={this.handleChange}>
            {categories.map((item) => <option key={item} value={item}>{item}</option>)}
          </select>
        </div>
        <button type="submit" > Submit </button>
      </form>
    );
  }
}

// connect bookForm component to dispatch
const mapDispatchToProps = (dispatch) => ({
  handleAdding: (book) => {
    dispatch(creatBookAction(book));
  },
});

BookForm = connect(null, mapDispatchToProps)(BookForm);

export default BookForm;
